import React, { useState } from 'react'
import Card from "../Ul/Card"
import "./AddUser.css"
import Wrapper from "../helper/Wrapper"
import  Button  from '../Ul/Button'
import ErrorModal from '../Ul/ErrorModal'
function AddUser(props) {
   
const [enteredUsername,setEnteredUsername]=useState('')
const [enteredAge,setEnteredAge]=useState('')
const [error,setError]=useState()


const submitHandler=(event)=>{
event.preventDefault()

if(enteredUsername.trim().length===0 || enteredAge.trim().length===0)
{
    setError({
        title:'Invalid Input',
        message:'Please Enter Valid Username & Age'
    })

    return 
}
if(+enteredAge< 1)
{
    setError({
        title:'Invalid Age',
        message:'Please Enter Valid Age(age>0)'
    })
    return
}


props.onAddUser(enteredUsername,enteredAge)
setEnteredUsername('')
setEnteredAge('')

if(+enteredAge>=60){

    alert('Young')
}

}

const usernameHandler=(event)=>{
 setEnteredUsername(event.target.value)
}
const ageHandler=(event)=>{
    setEnteredAge(event.target.value)
   }

const errorHandler=()=>{
    setError(null)
}

  return (
    <Wrapper>
    {error && <ErrorModal title={error.title} message={error.message} onError={errorHandler}/>}
    <Card className="input">
    <form onSubmit={submitHandler}>
    <label htmlFor='username'>userName</label>
    <input type='text' id='username' value={enteredUsername} onChange={usernameHandler}/>
    <label htmlFor='age'>age</label>
    <input type='number' id='age' value={enteredAge} onChange={ageHandler}/>
   <Button type='submit'>Add User</Button>
    </form>
    </Card>
    </Wrapper>
  )
}

export default AddUser
